#№5 Найти тему, в которой было выпущено больше всего книг. 

select A.ThemeId, T.theme, A.Bookcount from (select IDT as ThemeID, count(IDT) as Bookcount from BT group by IDT) as A inner join Theme as T on T.IDT = A.themeid where A.bookcount = (select max(A.bookcount) from (select IDT as ThemeID, count(IDT) as Bookcount from BT group by IDT) as A);

